package model;import java.awt.Color;import java.awt.Component;import java.awt.Dimension;import javax.swing.BorderFactory;import javax.swing.JLabel;import javax.swing.border.Border;public interface IConstants {    public static final Border    borderDefault       = BorderFactory.createEmptyBorder();    public static final Border    borderDone          = BorderFactory.createLineBorder(Color.GREEN);    public static final Border    borderLevel         = BorderFactory.createLineBorder(Color.YELLOW);    public static final Border    borderOther         = BorderFactory.createLineBorder(Color.CYAN);    public static final Border    borderPicking       = BorderFactory.createLineBorder(Color.RED);    public static final Border    borderProccess      = BorderFactory.createLineBorder(Color.MAGENTA);    public static final byte      continues           = -125;    public static final Color     defAult             = Color.LIGHT_GRAY;    public static final Color     done                = Color.CYAN;    public static final Color     level               = Color.MAGENTA;    public static final byte      normal              = -128;    public static final int       numAcceptAnimation  = 200;    public static final int       numAcceptShowNumber = 25;    public static final Color     other               = Color.PINK;    public static final byte      pause               = -126;    public static final Color     picking             = Color.ORANGE;    public static final Color     process             = Color.YELLOW;    public static final Dimension sizeBtn             = new Dimension(100, 35);    public static final Dimension sizeCbo             = new Dimension(130, 23);    public static final Dimension sizeLbl             = new Dimension(70, 23);    public static final byte      starting            = -127;    public static void ColorDefault(Column column) throws InterruptedException {        JLabel lbl = column.getLblValue();        lbl.setBackground(defAult);        lbl.setBorder(borderDefault);    }    public static void ColorDone(Column column) throws InterruptedException {        JLabel pn = column.getLblValue();        pn.setBackground(done);        pn.setBorder(borderDone);    }    public static void ColorLevel(Column column) throws InterruptedException {        JLabel pn = column.getLblValue();        pn.setBackground(level);        pn.setBorder(borderLevel);    }    public static void ColorOther(Column column) throws InterruptedException {        JLabel pn = column.getLblValue();        pn.setBackground(other);        pn.setBorder(borderOther);    }    public static void ColorPicking(Column column) throws InterruptedException {        JLabel pn = column.getLblValue();        pn.setBackground(picking);        pn.setBorder(borderPicking);    }    public static void ColorProccess(Column column) throws InterruptedException {        JLabel pn = column.getLblValue();        pn.setBackground(process);        pn.setBorder(borderProccess);    }    public static void refresh(Column t) throws InterruptedException {        refresh(((t.getLblValue())));        refresh(((t.getPnColumn())));    }    public static void refresh(Component t) throws InterruptedException {        synchronized(t) {            t.revalidate();            t.repaint();            Thread.sleep(1L);        }    }}