package model;import javax.swing.JOptionPane;public interface IText {    public static int showConfirm(Object msg, int msgOption) {        System.out.println("----------Xác Nhận?----------");        return JOptionPane.showConfirmDialog(null, msg, "Xác Nhận?", msgOption);    }    public static void showMessage(Object msg, int msgTyped) {        JOptionPane.showMessageDialog(null, msg, "Thông Báo!", msgTyped);        System.out.println("----------Thông Báo!---------");    }    public static void showText(Object msg, boolean b) {        if(b) {            System.out.println((new StringBuilder()).append(msg).append(" Thành Công!").toString());            showMessage((new StringBuilder()).append(msg).append(" Thành Công!.").toString(), 1);        } else {            System.err.println((new StringBuilder()).append(msg).append(" Thất Bại!.").toString());            showMessage((new StringBuilder()).append(msg).append(" Thất Bại!.").toString(), 0);        }    }}